import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SessionManager from './SessionManager';
import Session from './Session';

// Create an axios instance
const api = axios.create({
  baseURL: 'http://localhost:5000',
});

function App() {
  const [sessions, setSessions] = useState([]);

  const fetchSessions = async () => {
    try {
      const response = await api.get('/sessions');
      if (Array.isArray(response.data)) {
        setSessions(response.data);
      } else {
        console.warn('Session data is not an array:', response.data);
        setSessions([response.data]);
      }
    } catch (error) {
      console.error('Error fetching sessions:', error);
    }
  };

  useEffect(() => {
    fetchSessions();
  }, []);

  const createSession = async () => {
    try {
      const response = await api.post('/sessions');
      setSessions((prevSessions) => [...prevSessions, response.data]);
    } catch (error) {
      console.error('Error creating session:', error);
    }
  };

  const deleteSession = async (sessionId) => {
    try {
      await api.delete(`/sessions/${sessionId}`);
      setSessions((prevSessions) =>
        prevSessions.filter((session) => session.sessionId !== sessionId)
      );
    } catch (error) {
      console.error('Error deleting session:', error);
    }
  };

  return (
    <div className="App">
      <SessionManager onCreateSession={createSession} />
      {sessions.map((session) => (
        <Session key={session.sessionId} session={session} onDelete={deleteSession} />
      ))}
    </div>
  );
}

export default App;
