import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SessionManager from './SessionManager';
import Session from './Session';

function App() {
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    const fetchSessions = async () => {
      const response = await axios.get('http://localhost:3001/sessions');
      setSessions(response.data);
    };

    fetchSessions();
  }, []);

  const createSession = async () => {
    const response = await axios.post('http://localhost:3001/sessions');
    setSessions((prevSessions) => [...prevSessions, response.data]);
  };

  const deleteSession = async (sessionId) => {
    await axios.delete(`http://localhost:3001/sessions/${sessionId}`);
    setSessions((prevSessions) =>
      prevSessions.filter((session) => session.sessionId !== sessionId)
    );
  };

  return (
    <div className="App">
      <SessionManager onCreateSession={createSession} />
      {sessions.map((session) => (
        <Session key={session.sessionId} session={session} onDelete={deleteSession} />
      ))}
    </div>
  );
}

export default App;
