import React, { useEffect, useState } from 'react';
import axios from 'axios';
import moment from 'moment';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap-grid.min.css';
import { Col } from 'react-bootstrap';
import './App.css';

axios.defaults.withCredentials = true;

const API_BASE_URL = 'http://localhost:5000';

function YesSessionHome({ userId, expiry, setExpiry, startSession, killSession }) {
  const [isLoaded, setIsLoaded] = useState(false);

  const destorySession = async  () => {
    try {
      await axios.get(`${API_BASE_URL}/sessions/destroy`);
    } catch (error) {
      console.error('Error deleting session:', error);
    }
  };

  const createSession = async  () => {
    try {
      await axios.get(`${API_BASE_URL}/sessions/create`);
    } catch (error) {
      console.error('Error creating session:', error);
    }
  };

  const handleNewSessionClick = async () => {
    try {
      await axios.get(`${API_BASE_URL}/sessions/create`);
      startSession();
    } catch (error) {
      killSession();
      console.error('Error creating new session:', error);
    }
  };

  const handleDeleteSessionClick = async () => {
    try {
      destorySession();
      killSession();
    } catch (error) {
      console.error('Error deleting session:', error);
    }
  };

  useEffect(() => {
    if (expiry && userId) {
      const intervalId = setInterval(() => {
        const now = moment();
        const expiryTime = moment(expiry);
        const diff = expiryTime.diff(now);
        const duration = moment.duration(diff);
        const seconds = duration.seconds();

        console.log(`Expiry in ${seconds} seconds.`);

        if (isLoaded && seconds <= 0) {
          // Session has expired, redirect to NoSessionHome
          killSession();
        } else {
          setExpiry(expiryTime.add(500, 'millisecond').format());
        }
      }, 1000);

      // Cleanup function: clear the interval when the component unmounts or when expiry changes
      return () => clearInterval(intervalId);
    } else {
      console.log('No expiry or userId set. Expiry: ' + expiry + ', userId: ' + userId);
      setExpiry(null);
    }

    setIsLoaded(true);
  }, [userId, expiry, setExpiry, killSession, isLoaded]);  

  return (
      <>
        <Col className="col-md-12">
          <p>
            Welcome back! Your user ID is: <span style={{ fontWeight: 'bold' }}>{userId}</span>
          </p>
          <p>
            Session expires in:{' '}
            {expiry ? (
              <span>
                {moment.duration(moment(expiry).diff(moment())).seconds()}
              </span>
            ) : (
              'unknown'
            )}
          </p>
          <button className="button create" onClick={handleNewSessionClick}>
            Create session
          </button>
          <button className="button delete" onClick={handleDeleteSessionClick}>
            Delete Session
          </button>
        </Col>
      </>
  );
}

export default YesSessionHome;
