// src/App.js
import React, { useState } from 'react';
import Draggable from 'react-draggable';
import './App.css';

const App = () => {
  const [playheadPosition, setPlayheadPosition] = useState(0);

  const handleDrag = (e, ui) => {
    // Update playhead position based on the drag
    setPlayheadPosition(ui.x);
  };

  return (
    <div className="App">
      <div className="timeline">
        {/* Main clip */}
        <div className="clip main-clip" style={{ width: '300px' }}></div>

        {/* Three tracks with clips */}
        <div className="track">
          <div className="clip" style={{ width: '100px' }}></div>
          <div className="clip" style={{ width: '150px' }}></div>
        </div>

        <div className="track">
          <div className="clip" style={{ width: '120px' }}></div>
          {/* Add more clips as needed */}
        </div>

        <div className="track">
          {/* Add clips for the third track */}
        </div>

        {/* Playhead */}
        <Draggable
          axis="x"
          bounds="parent"
          position={{ x: playheadPosition, y: 0 }}
          onDrag={handleDrag}
        >
          <div className="playhead"></div>
        </Draggable>
      </div>
    </div>
  );
};

export default App;
