import React, { useState } from 'react';
import styled from 'styled-components';

const TimelineContainer = styled.div`
  position: relative;
  /* Add other styling as needed */
`;

const Playhead = styled.div`
  background-color: blue;
  height: 100%;
  width: 2px;
  position: absolute;
  z-index: 1;
`;

const Clip = styled.div`
  background-color: lightgray;
  height: 30px;
  width: ${props => props.duration}px;
  position: absolute;
  /* Additional styling properties as needed */
`;

const VideoClip = styled.video`
  /* Styling for the video element */
  height: 100%;
  width: 100%;
  object-fit: cover; /* Adjust as needed */
`;

const Timeline = ({ clips }) => {
  const [playheadPosition, setPlayheadPosition] = useState(0);

  const handlePlayheadMove = (e, clipIndex) => {
    const containerRect = e.target.getBoundingClientRect();
    const clickPosition = e.clientX - containerRect.left;
    const newPlayheadPosition = Math.max(0, Math.min(clickPosition, clips[clipIndex].duration));
    setPlayheadPosition(newPlayheadPosition);
  };

  const handlePlayheadDrag = (e, clipIndex) => {
    const containerRect = e.target.getBoundingClientRect();
    const newPlayheadPosition = Math.max(0, Math.min(e.clientX - containerRect.left, clips[clipIndex].duration));
    setPlayheadPosition(newPlayheadPosition);
  };

  const handlePlayheadDragEnd = () => {
    document.removeEventListener('mousemove', handlePlayheadDrag);
    document.removeEventListener('mouseup', handlePlayheadDragEnd);
  };

  return (
    <TimelineContainer>
      <Playhead style={{ left: `${playheadPosition}px` }} />
      {clips.map((clip, index) => (
        <Clip
          key={index}
          duration={clip.duration}
          style={{ top: '10px', left: '50px' }}
          onMouseDown={e => {
            handlePlayheadMove(e, index);
            document.addEventListener('mousemove', e => handlePlayheadDrag(e, index));
            document.addEventListener('mouseup', handlePlayheadDragEnd);
          }}
        >
          <VideoClip controls>
            <source src={clip.src} type="video/mp4" />
            Your browser does not support the video tag.
          </VideoClip>
        </Clip>
      ))}
    </TimelineContainer>
  );
};

export default Timeline;
