import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap-grid.min.css';
import { Container, Row, Col, Navbar, Nav, NavItem, NavLink } from 'react-bootstrap';import './App.css';

function App() {
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(storedUserId);
    } else {
      axios.get('http://localhost:5000/').then((response) => {
        setUserId(response.data.userId);
      });
    }
  }, []);

  const handleNewSessionClick = () => {
    axios.get('http://localhost:5000/sessions/create').then((response) => {
      setUserId(response.data.userId);
      localStorage.setItem('userId', response.data.userId);
    });
  };

  const handleDeleteSessionClick = () => {
    axios.get('http://localhost:5000/sessions/destroy').then((response) => {
      setUserId(null);
      localStorage.removeItem('userId');
    });
  };

  return (
    <Container className="main-container">
      <Row className="">
        <Col className="col-md-12">
          <h1 style={{ color: 'rgb(141, 91, 179)', textAlign: 'center' }}>Session Spawner</h1>
          {userId ? (
            <p>Welcome back! Your user ID is: {userId}</p>
          ) : (
            <p>Welcome! No user ID in the session.</p>
          )}
          <button className="button create" onClick={handleNewSessionClick}>Create session</button>
          <button className="button delete" onClick={handleDeleteSessionClick}>Delete Session</button>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
