// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/').then((response) => {
      setUserId(response.data.userId);
    });
  }, []); // empty dependency array

  useEffect(() => {
    if (!userId) {
      axios.get('http://localhost:5000/sessions/destroy').then((response) => {
        setUserId(null);
      });
    }
  }, [userId]);

  const handleNewSessionClick = () => {
    axios.get('http://localhost:5000/sessions/create').then((response) => {
      setUserId(response.data.userId);
      localStorage.setItem('userId', response.data.userId);
    });
  };

  const handleDeleteSessionClick = () => {
    axios.get('http://localhost:5000/sessions/destroy').then((response) => {
      setUserId(null);
    });
  };

  return (
    <div>
      <h1>Home Page</h1>
      {userId ? (
        <p>Welcome back! Your user ID is: {userId}</p>
      ) : (
        <p>Welcome! No user ID in the session.</p>
      )}
      <button onClick={handleNewSessionClick}>New Session</button>
      <button onClick={handleDeleteSessionClick}>Delete Session</button>
    </div>
  );
}

export default App;
